                                    </tr>
                                ) : (
                                    trainerPerformanceData.map((item, index) => (
                                        <tr key={index}>
                                            <td>{item.Trainer_Name}</td>
                                            <td>{item.Total_Members || 0}</td>
                                            <td>{item.Total_Sessions || 0}</td>
