import React, { useEffect, useState } from 'react';
import MemberList from '../components/members/MemberList';
import MemberForm from '../components/members/MemberForm';
import { getMembers } from '../services/memberService';

const Members = () => {
    const [members, setMembers] = useState([]);
    const [selectedMember, setSelectedMember] = useState(null);

    useEffect(() => {
        const fetchMembers = async () => {
            const data = await getMembers();
            setMembers(data);
        };

        fetchMembers();
    }, []);

    const handleMemberSelect = (member) => {
        setSelectedMember(member);
    };

    const handleMemberFormSubmit = (member) => {
        setMembers((prevMembers) => {
            if (selectedMember) {
                return prevMembers.map((m) => (m.id === member.id ? member : m));
            }
            return [...prevMembers, member];
        });
        setSelectedMember(null);
    };

    return (
        <div>
            <h1>Members</h1>
            <MemberForm member={selectedMember} onSubmit={handleMemberFormSubmit} />
            <MemberList members={members} onSelect={handleMemberSelect} />
        </div>
    );
};

export default Members;
