import React from 'react';
import { NavLink } from 'react-router-dom';

const Sidebar = () => {
    const menuItems = [
        { path: '/', icon: 'üè†', label: 'Dashboard' },
        { path: '/members', icon: 'üë•', label: 'Members' },
        { path: '/trainers', icon: 'üèãÔ∏è', label: 'Trainers' },
        { path: '/sessions', icon: 'üìÖ', label: 'Sessions' },
        { path: '/payments', icon: 'üí≥', label: 'Payments' },
        { path: '/equipment', icon: 'üèÉ', label: 'Equipment' },
        { path: '/attendance', icon: '‚úÖ', label: 'Attendance' },
        { path: '/feedback', icon: 'üí¨', label: 'Feedback' },
        { path: '/reports', icon: 'üìä', label: 'Reports' }
    ];

    return (
        <div className="w-64 bg-gradient-to-b from-gray-800 to-gray-700 text-white h-screen fixed left-0 top-0 overflow-y-auto shadow-2xl transition-transform duration-300 z-[1000] lg:w-64 md:w-20 sm:w-64 sm:-translate-x-full">
            <div className="p-6 bg-gradient-primary text-center border-b-4 border-white border-opacity-10 sticky top-0 z-10">
                <div className="text-5xl mb-3 animate-pulse">üí™</div>
                <h2 className="text-2xl font-bold tracking-wide lg:block md:text-xs sm:text-2xl">Gym Manager</h2>
            </div>
            <nav className="py-5">
                <ul className="list-none p-0 m-0">
                    {menuItems.map((item) => (
                        <li key={item.path} className="mb-1 px-2.5">
                            <NavLink 
                                to={item.path} 
                                className={({ isActive }) => 
                                    `flex items-center py-3.5 px-4 text-white text-opacity-80 no-underline transition-all duration-300 text-base font-medium rounded-xl relative overflow-hidden hover:bg-white hover:bg-opacity-10 hover:text-white hover:translate-x-1 before:content-[''] before:absolute before:left-0 before:top-0 before:h-full before:w-1 before:bg-gradient-primary before:scale-y-0 before:transition-transform before:duration-300 hover:before:scale-y-100 ${
                                        isActive ? 'bg-gradient-to-r from-purple-600 from-30% to-purple-500 to-100% bg-opacity-30 text-white font-semibold shadow-lg shadow-purple-600/30 before:scale-y-100' : ''
                                    }`
                                }
                                end={item.path === '/'}
                            >
                                <span className="text-2xl mr-4 inline-flex items-center justify-center min-w-[30px] lg:mr-4 md:mr-0">
                                    {item.icon}
                                </span>
                                <span className="flex-1 lg:inline md:hidden sm:inline">{item.label}</span>
                            </NavLink>
                        </li>
                    ))}
                </ul>
            </nav>
        </div>
    );
};

export default Sidebar;
