import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { getMembers } from '../services/memberService';
import { getTrainers } from '../services/trainerService';
import { getSessions} from '../services/sessionService';
import { getPayments } from '../services/paymentService';

const Home = () => {
    const [stats, setStats] = useState({
        totalMembers: 0,
        totalTrainers: 0,
        totalSessions: 0,
        totalRevenue: 0,
        activeMembers: 0,
        upcomingSessions: 0
    });
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchDashboardData();
    }, []);

    const fetchDashboardData = async () => {
        try {
            setLoading(true);
            const [members, trainers, sessions, payments] = await Promise.all([
                getMembers().catch(() => []),
                getTrainers().catch(() => []),
                getSessions().catch(() => []),
                getPayments().catch(() => [])
            ]);

            const activeMembers = members.filter(m => m.status === 'Active').length;
            const totalRevenue = payments.reduce((sum, p) => sum + (parseFloat(p.Amount) || 0), 0);

            setStats({
                totalMembers: members.length,
                totalTrainers: trainers.length,
                totalSessions: sessions.length,
                totalRevenue: totalRevenue,
                activeMembers: activeMembers,
                upcomingSessions: sessions.length
            });
        } catch (error) {
            console.error('Error fetching dashboard data:', error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="px-5 max-w-7xl mx-auto">
            <div className="bg-gradient-primary text-white py-10 px-10 rounded-xl mb-9 text-center shadow-2xl">
                <h1 className="text-white text-5xl mb-2.5 font-bold">Welcome to Gym Management System</h1>
                <p className="text-lg opacity-90">Manage your gym operations efficiently</p>
            </div>

            {loading ? (
                <div className="text-center py-10 text-lg text-gray-600">Loading dashboard...</div>
            ) : (
                <>
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-9">
                        <div className="bg-white p-6 rounded-xl shadow-xl flex flex-col gap-4 transition-all duration-300 hover:-translate-y-1 hover:shadow-2xl border-l-4 border-blue-500">
                            <div className="text-5xl">üë•</div>
                            <div>
                                <h3 className="text-4xl text-gray-800 m-0 font-bold">{stats.totalMembers}</h3>
                                <p className="text-sm text-gray-600 my-1">Total Members</p>
                                <small className="text-xs text-gray-500">{stats.activeMembers} Active</small>
                            </div>
                            <Link to="/members" className="text-blue-500 no-underline text-sm font-medium transition-colors hover:text-blue-700">View All ‚Üí</Link>
                        </div>

                        <div className="bg-white p-6 rounded-xl shadow-xl flex flex-col gap-4 transition-all duration-300 hover:-translate-y-1 hover:shadow-2xl border-l-4 border-red-500">
                            <div className="text-5xl">üí™</div>
                            <div>
                                <h3 className="text-4xl text-gray-800 m-0 font-bold">{stats.totalTrainers}</h3>
                                <p className="text-sm text-gray-600 my-1">Total Trainers</p>
                            </div>
                            <Link to="/trainers" className="text-blue-500 no-underline text-sm font-medium transition-colors hover:text-blue-700">View All ‚Üí</Link>
                        </div>

                        <div className="bg-white p-6 rounded-xl shadow-xl flex flex-col gap-4 transition-all duration-300 hover:-translate-y-1 hover:shadow-2xl border-l-4 border-orange-500">
                            <div className="text-5xl">üìÖ</div>
                            <div>
                                <h3 className="text-4xl text-gray-800 m-0 font-bold">{stats.totalSessions}</h3>
                                <p className="text-sm text-gray-600 my-1">Total Sessions</p>
                                <small className="text-xs text-gray-500">{stats.upcomingSessions} Upcoming</small>
                            </div>
                            <Link to="/sessions" className="text-blue-500 no-underline text-sm font-medium transition-colors hover:text-blue-700">View All ‚Üí</Link>
                        </div>

                        <div className="bg-white p-6 rounded-xl shadow-xl flex flex-col gap-4 transition-all duration-300 hover:-translate-y-1 hover:shadow-2xl border-l-4 border-green-500">
                            <div className="text-5xl">üí∞</div>
                            <div>
                                <h3 className="text-4xl text-gray-800 m-0 font-bold">${stats.totalRevenue.toFixed(2)}</h3>
                                <p className="text-sm text-gray-600 my-1">Total Revenue</p>
                            </div>
                            <Link to="/payments" className="text-blue-500 no-underline text-sm font-medium transition-colors hover:text-blue-700">View Details ‚Üí</Link>
                        </div>
                    </div>

                    <div className="bg-white p-8 rounded-xl shadow-xl">
                        <h2 className="mb-5 text-gray-800 text-2xl font-bold">Quick Actions</h2>
                        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
                            <Link to="/members" className="flex items-center gap-4 p-5 bg-gray-100 rounded-lg no-underline text-gray-800 transition-all duration-300 hover:bg-gray-200 hover:translate-x-1">
                                <span className="text-4xl">‚ûï</span>
                                <span className="font-medium">Add New Member</span>
                            </Link>
                            <Link to="/sessions" className="flex items-center gap-4 p-5 bg-gray-100 rounded-lg no-underline text-gray-800 transition-all duration-300 hover:bg-gray-200 hover:translate-x-1">
                                <span className="text-4xl">üìù</span>
                                <span className="font-medium">Schedule Session</span>
                            </Link>
                            <Link to="/payments" className="flex items-center gap-4 p-5 bg-gray-100 rounded-lg no-underline text-gray-800 transition-all duration-300 hover:bg-gray-200 hover:translate-x-1">
                                <span className="text-4xl">üí≥</span>
                                <span className="font-medium">Record Payment</span>
                            </Link>
                            <Link to="/attendance" className="flex items-center gap-4 p-5 bg-gray-100 rounded-lg no-underline text-gray-800 transition-all duration-300 hover:bg-gray-200 hover:translate-x-1">
                                <span className="text-4xl">‚úì</span>
                                <span className="font-medium">Mark Attendance</span>
                            </Link>
                        </div>
                    </div>
                </>
            )}
        </div>
    );
};

export default Home;
