import React from 'react';
import { Link, useHistory, useLocation } from 'react-router-dom';

const Header = ({ onLogout }) => {
    const history = useHistory();
    const location = useLocation();

    const handleLogout = () => {
        if (onLogout) {
            onLogout();
        }
        history.push('/login');
    };

    // Get page title based on current route
    const getPageTitle = () => {
        const path = location.pathname;
        const titles = {
            '/': 'Dashboard',
            '/members': 'Members Management',
            '/trainers': 'Trainers Management',
            '/sessions': 'Training Sessions',
            '/payments': 'Payment Records',
            '/equipment': 'Equipment Management',
            '/attendance': 'Attendance Tracking',
            '/feedback': 'Feedback Management',
            '/reports': 'Reports & Analytics'
        };
        return titles[path] || 'Gym Management System';
    };

    return (
        <header className="bg-gradient-primary text-white px-8 flex justify-between items-center shadow-2xl fixed top-0 left-64 right-0 h-[70px] z-[900] lg:left-64 md:left-20 sm:left-0">
            <div>
                <h1 className="m-0 text-2xl font-semibold text-white lg:text-2xl sm:text-base">{getPageTitle()}</h1>
            </div>
            <div className="flex gap-4 items-center">
                <button 
                    onClick={handleLogout} 
                    className="bg-white bg-opacity-20 text-white border-2 border-white border-opacity-50 py-2.5 px-6 rounded-full cursor-pointer text-sm transition-all duration-300 font-semibold backdrop-blur-lg flex items-center gap-2 hover:bg-white hover:bg-opacity-30 hover:border-white hover:-translate-y-0.5 hover:shadow-xl sm:py-2 sm:px-4 sm:text-xs"
                >
                    <span className="text-base">ðŸšª</span>
                    <span className="sm:hidden">Logout</span>
                </button>
            </div>
        </header>
    );
};

export default Header;
