import React, { useEffect, useState } from 'react';
import { getMembers, createMember, updateMember, deleteMember } from '../services/memberService';
import Loader from '../components/common/Loader';
import '../styles/Members.css';

const Members = () => {
    const [members, setMembers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [showForm, setShowForm] = useState(false);
    const [selectedMember, setSelectedMember] = useState(null);
    const [formData, setFormData] = useState({
        Name: '',
        Gender: '',
        Phone_Num: '',
        Address: '',
        Join_Date: '',
        status: 'Active',
        membership_expiry: ''
    });
    const [message, setMessage] = useState({ type: '', text: '' });

    useEffect(() => {
        fetchMembers();
    }, []);

    const fetchMembers = async () => {
        try {
            setLoading(true);
            const data = await getMembers();
            setMembers(data || []);
        } catch (error) {
            console.error('Error fetching members:', error);
            setMessage({ type: 'error', text: 'Failed to fetch members. Please try again.' });
            setMembers([]);
        } finally {
            setLoading(false);
        }
    };

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleEdit = (member) => {
        setSelectedMember(member);
        setFormData({
            Name: member.Name || '',
            Gender: member.Gender || '',
            Phone_Num: member.Phone_Num || '',
            Address: member.Address || '',
            Join_Date: member.Join_Date ? member.Join_Date.split('T')[0] : '',
            status: member.status || 'Active',
            membership_expiry: member.membership_expiry ? member.membership_expiry.split('T')[0] : ''
        });
        setShowForm(true);
    };

    const handleDelete = async (memberId) => {
        if (window.confirm('Are you sure you want to delete this member?')) {
            try {
                await deleteMember(memberId);
                setMessage({ type: 'success', text: 'Member deleted successfully' });
                fetchMembers();
            } catch (error) {
                setMessage({ type: 'error', text: 'Failed to delete member' });
            }
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (selectedMember) {
                await updateMember(selectedMember.Member_id, formData);
                setMessage({ type: 'success', text: 'Member updated successfully!' });
            } else {
                await createMember(formData);
                setMessage({ type: 'success', text: 'Member created successfully!' });
            }
            fetchMembers();
            handleCancel();
            setTimeout(() => setMessage({ type: '', text: '' }), 3000);
        } catch (error) {
            console.error('Error saving member:', error);
            setMessage({ type: 'error', text: error.response?.data?.message || 'Failed to save member. Please check all fields.' });
        }
    };

    const handleCancel = () => {
        setShowForm(false);
        setSelectedMember(null);
        setFormData({
            Name: '',
            Gender: '',
            Phone_Num: '',
            Address: '',
            Join_Date: '',
            status: 'Active',
            membership_expiry: ''
        });
    };

    return (
        <div className="members-page">
            <div className="page-header">
                <h1>Members Management</h1>
                <button className="btn-add" onClick={() => setShowForm(!showForm)}>
                    {showForm ? '‚úï Cancel' : '‚ûï Add New Member'}
                </button>
            </div>

            {message.text && (
                <div className={`alert alert-${message.type}`}>
                    {message.text}
                    <button onClick={() => setMessage({ type: '', text: '' })}>‚úï</button>
                </div>
            )}

            {showForm && (
                <div className="member-form-container">
                    <h2>{selectedMember ? 'Edit Member' : 'Add New Member'}</h2>
                    <form onSubmit={handleSubmit} className="member-form">
                        <div className="form-row">
                            <div className="form-group">
                                <label>Name *</label>
                                <input
                                    type="text"
                                    name="Name"
                                    value={formData.Name}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label>Gender</label>
                                <select name="Gender" value={formData.Gender} onChange={handleInputChange}>
                                    <option value="">Select Gender</option>
                                    <option value="Male">Male</option>
                                    <option value="Female">Female</option>
                                    <option value="Other">Other</option>
                                </select>
                            </div>
                        </div>

                        <div className="form-row">
                            <div className="form-group">
                                <label>Phone Number</label>
                                <input
                                    type="tel"
                                    name="Phone_Num"
                                    value={formData.Phone_Num}
                                    onChange={handleInputChange}
                                />
                            </div>
                            <div className="form-group">
                                <label>Status</label>
                                <select name="status" value={formData.status} onChange={handleInputChange}>
                                    <option value="Active">Active</option>
                                    <option value="Inactive">Inactive</option>
                                </select>
                            </div>
                        </div>

                        <div className="form-group">
                            <label>Address</label>
                            <textarea
                                name="Address"
                                value={formData.Address}
                                onChange={handleInputChange}
                                rows="2"
                            />
                        </div>

                        <div className="form-row">
                            <div className="form-group">
                                <label>Join Date *</label>
                                <input
                                    type="date"
                                    name="Join_Date"
                                    value={formData.Join_Date}
                                    onChange={handleInputChange}
                                    required
                                />
                            </div>
                            <div className="form-group">
                                <label>Membership Expiry</label>
                                <input
                                    type="date"
                                    name="membership_expiry"
                                    value={formData.membership_expiry}
                                    onChange={handleInputChange}
                                />
                            </div>
                        </div>

                        <div className="form-actions">
                            <button type="submit" className="btn-submit">
                                {selectedMember ? 'Update Member' : 'Create Member'}
                            </button>
                            <button type="button" className="btn-cancel" onClick={handleCancel}>
                                Cancel
                            </button>
                        </div>
                    </form>
                </div>
            )}

            {loading ? (
                <div className="loading">Loading members...</div>
            ) : (
                <div className="members-table-container">
                    <table className="members-table">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Name</th>
                                <th>Gender</th>
                                <th>Phone</th>
                                <th>Address</th>
                                <th>Join Date</th>
                                <th>Status</th>
                                <th>Expiry</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {members.length === 0 ? (
                                <tr>
                                    <td colSpan="9" style={{ textAlign: 'center' }}>
                                        No members found. Add your first member!
                                    </td>
                                </tr>
                            ) : (
                                members.map((member) => (
                                    <tr key={member.Member_id}>
                                        <td>{member.Member_id}</td>
                                        <td>{member.Name}</td>
                                        <td>{member.Gender}</td>
                                        <td>{member.Phone_Num}</td>
                                        <td>{member.Address}</td>
                                        <td>{member.Join_Date ? new Date(member.Join_Date).toLocaleDateString() : 'N/A'}</td>
                                        <td>
                                            <span className={`status-badge ${member.status?.toLowerCase()}`}>
                                                {member.status}
                                            </span>
                                        </td>
                                        <td>{member.membership_expiry ? new Date(member.membership_expiry).toLocaleDateString() : 'N/A'}</td>
                                        <td className="actions">
                                            <button
                                                className="btn-edit"
                                                onClick={() => handleEdit(member)}
                                                title="Edit"
                                            >
                                                ‚úèÔ∏è
                                            </button>
                                            <button
                                                className="btn-delete"
                                                onClick={() => handleDelete(member.Member_id)}
                                                title="Delete"
                                            >
                                                üóëÔ∏è
                                            </button>
                                        </td>
                                    </tr>
                                ))
                            )}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
};

export default Members;
