import React, { useEffect, useState } from 'react';
import { getMembers, createMember, updateMember, deleteMember } from '../services/memberService';
import Loader from '../components/common/Loader';

const Members = () => {
    const [members, setMembers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [showForm, setShowForm] = useState(false);
    const [selectedMember, setSelectedMember] = useState(null);
    const [formData, setFormData] = useState({
        Name: '',
        Gender: '',
        Phone_Num: '',
        Address: '',
        Join_Date: '',
        status: 'Active',
        membership_expiry: ''
    });
    const [message, setMessage] = useState({ type: '', text: '' });

    useEffect(() => {
        fetchMembers();
    }, []);

    const fetchMembers = async () => {
        try {
            setLoading(true);
            const data = await getMembers();
            setMembers(data || []);
        } catch (error) {
            console.error('Error fetching members:', error);
            setMessage({ type: 'error', text: 'Failed to fetch members. Please try again.' });
            setMembers([]);
        } finally {
            setLoading(false);
        }
    };

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleEdit = (member) => {
        setSelectedMember(member);
        setFormData({
            Name: member.Name || '',
            Gender: member.Gender || '',
            Phone_Num: member.Phone_Num || '',
            Address: member.Address || '',
            Join_Date: member.Join_Date ? member.Join_Date.split('T')[0] : '',
            status: member.status || 'Active',
            membership_expiry: member.membership_expiry ? member.membership_expiry.split('T')[0] : ''
        });
        setShowForm(true);
    };

    const handleDelete = async (memberId) => {
        if (window.confirm('Are you sure you want to delete this member? This action cannot be undone.')) {
            try {
                await deleteMember(memberId);
                setMessage({ type: 'success', text: 'Member deleted successfully!' });
                fetchMembers();
                setTimeout(() => setMessage({ type: '', text: '' }), 3000);
            } catch (error) {
                console.error('Error deleting member:', error);
                setMessage({ type: 'error', text: 'Failed to delete member. Please try again.' });
            }
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (selectedMember) {
                await updateMember(selectedMember.Member_id, formData);
                setMessage({ type: 'success', text: 'Member updated successfully!' });
            } else {
                await createMember(formData);
                setMessage({ type: 'success', text: 'Member created successfully!' });
            }
            fetchMembers();
            handleCancel();
            setTimeout(() => setMessage({ type: '', text: '' }), 3000);
        } catch (error) {
            console.error('Error saving member:', error);
            setMessage({ type: 'error', text: error.response?.data?.message || 'Failed to save member. Please check all fields.' });
        }
    };

    const handleCancel = () => {
        setShowForm(false);
        setSelectedMember(null);
        setFormData({
            Name: '',
            Gender: '',
            Phone_Num: '',
            Address: '',
            Join_Date: '',
            status: 'Active',
            membership_expiry: ''
        });
    };

    return (
        <div className="max-w-7xl mx-auto px-5">
            <div className="flex justify-between items-center mb-8 flex-wrap gap-4">
                <h1 className="text-4xl font-bold text-gray-800 m-0">Members Management</h1>
                {!showForm && (
                    <button 
                        onClick={() => setShowForm(true)}
                        className="bg-gradient-to-r from-purple-600 to-purple-800 text-white py-3.5 px-7 rounded-lg font-semibold text-base transition-all duration-300 shadow-lg shadow-purple-600/40 hover:-translate-y-0.5 hover:shadow-xl hover:shadow-purple-600/60"
                    >
                        âž• Add New Member
                    </button>
                )}
            </div>

            {message.text && (
                <div className={`p-4 px-6 rounded-lg mb-6 font-medium text-base animate-slideDown ${
                    message.type === 'success' 
                        ? 'bg-gradient-to-r from-green-500 to-green-600 text-white shadow-lg shadow-green-500/30' 
                        : 'bg-gradient-to-r from-red-500 to-red-600 text-white shadow-lg shadow-red-500/30'
                }`}>
                    {message.text}
                </div>
            )}

            {showForm && (
                    <div className="bg-white p-8 rounded-xl shadow-xl mb-8">
                        <h2 className="m-0 mb-6 text-gray-800 text-2xl font-semibold border-b-4 border-purple-600 pb-3">
                            {selectedMember ? 'Edit Member' : 'Add New Member'}
                        </h2>
                        <form onSubmit={handleSubmit}>
                            <div className="grid grid-cols-1 md:grid-cols-2 gap-5 mb-5">
                                <div className="flex flex-col">
                                    <label className="mb-2 text-gray-700 font-semibold text-sm">Full Name *</label>
                                    <input
                                        type="text"
                                        name="Name"
                                        value={formData.Name}
                                        onChange={handleInputChange}
                                        placeholder="John Doe"
                                        required
                                        className="p-3 px-4 border-2 border-gray-300 rounded-lg text-sm transition-all focus:outline-none focus:border-purple-600 focus:shadow-lg focus:shadow-purple-600/10"
                                    />
                                </div>
                                <div className="flex flex-col">
                                    <label className="mb-2 text-gray-700 font-semibold text-sm">Gender</label>
                                    <select 
                                        name="Gender" 
                                        value={formData.Gender} 
                                        onChange={handleInputChange}
                                        className="p-3 px-4 border-2 border-gray-300 rounded-lg text-sm transition-all focus:outline-none focus:border-purple-600 focus:shadow-lg focus:shadow-purple-600/10 bg-white"
                                    >
                                        <option value="">Select Gender</option>
                                        <option value="Male">Male</option>
                                        <option value="Female">Female</option>
                                        <option value="Other">Other</option>
                                    </select>
                                </div>
                            </div>

                            <div className="grid grid-cols-1 md:grid-cols-2 gap-5 mb-5">
                                <div className="flex flex-col">
                                    <label className="mb-2 text-gray-700 font-semibold text-sm">Phone Number *</label>
                                    <input
                                        type="tel"
                                        name="Phone_Num"
                                        value={formData.Phone_Num}
                                        onChange={handleInputChange}
                                        placeholder="+91 98765 43210"
                                        required
                                        className="p-3 px-4 border-2 border-gray-300 rounded-lg text-sm transition-all focus:outline-none focus:border-purple-600 focus:shadow-lg focus:shadow-purple-600/10"
                                    />
                                </div>
                                <div className="flex flex-col">
                                    <label className="mb-2 text-gray-700 font-semibold text-sm">Membership Status *</label>
                                    <select 
                                        name="status" 
                                        value={formData.status} 
                                        onChange={handleInputChange} 
                                        required
                                        className="p-3 px-4 border-2 border-gray-300 rounded-lg text-sm transition-all focus:outline-none focus:border-purple-600 focus:shadow-lg focus:shadow-purple-600/10 bg-white"
                                    >
                                        <option value="Active">Active</option>
                                        <option value="Inactive">Inactive</option>
                                    </select>
                                </div>
                            </div>

                            <div className="grid grid-cols-1 md:grid-cols-2 gap-5 mb-5">
                                <div className="flex flex-col">
                                    <label className="mb-2 text-gray-700 font-semibold text-sm">Join Date *</label>
                                    <input
                                        type="date"
                                        name="Join_Date"
                                        value={formData.Join_Date}
                                        onChange={handleInputChange}
                                        required
                                        className="p-3 px-4 border-2 border-gray-300 rounded-lg text-sm transition-all focus:outline-none focus:border-purple-600 focus:shadow-lg focus:shadow-purple-600/10"
                                    />
                                </div>
                                <div className="flex flex-col">
                                    <label className="mb-2 text-gray-700 font-semibold text-sm">Membership Expiry</label>
                                    <input
                                        type="date"
                                        name="membership_expiry"
                                        value={formData.membership_expiry}
                                        onChange={handleInputChange}
                                        className="p-3 px-4 border-2 border-gray-300 rounded-lg text-sm transition-all focus:outline-none focus:border-purple-600 focus:shadow-lg focus:shadow-purple-600/10"
                                    />
                                </div>
                            </div>

                            <div className="flex flex-col mb-5">
                                <label className="mb-2 text-gray-700 font-semibold text-sm">Address</label>
                                <textarea
                                    name="Address"
                                    value={formData.Address}
                                    onChange={handleInputChange}
                                    placeholder="Full address..."
                                    rows="3"
                                    className="p-3 px-4 border-2 border-gray-300 rounded-lg text-sm transition-all focus:outline-none focus:border-purple-600 focus:shadow-lg focus:shadow-purple-600/10 resize-vertical min-h-[80px]"
                                />
                            </div>

                            <div className="flex gap-4 mt-6 pt-5 border-t border-gray-300">
                                <button 
                                    type="submit" 
                                    className="bg-gradient-to-r from-purple-600 to-purple-800 text-white py-3.5 px-8 rounded-lg font-semibold text-base transition-all duration-300 shadow-lg shadow-purple-600/40 hover:-translate-y-0.5 hover:shadow-xl hover:shadow-purple-600/60"
                                >
                                    {selectedMember ? 'âœ“ Update Member' : 'âœ“ Create Member'}
                                </button>
                                <button 
                                    type="button" 
                                    onClick={handleCancel}
                                    className="bg-gray-500 text-white py-3.5 px-8 rounded-lg font-semibold text-base transition-all duration-300 hover:bg-gray-600 hover:-translate-y-0.5"
                                >
                                    âœ• Cancel
                                </button>
                            </div>
                        </form>
                    </div>
                )}

            {loading ? (
                <Loader />
            ) : (
                <div className="bg-white rounded-xl shadow-xl overflow-hidden mt-8">
                    <div className="overflow-x-auto">
                        <table className="w-full border-collapse">
                            <thead className="bg-gradient-to-r from-purple-600 to-purple-800">
                                <tr>
                                    <th className="p-4 text-left font-semibold text-sm text-white uppercase tracking-wide">ID</th>
                                    <th className="p-4 text-left font-semibold text-sm text-white uppercase tracking-wide">Name</th>
                                    <th className="p-4 text-left font-semibold text-sm text-white uppercase tracking-wide">Gender</th>
                                    <th className="p-4 text-left font-semibold text-sm text-white uppercase tracking-wide">Phone</th>
                                    <th className="p-4 text-left font-semibold text-sm text-white uppercase tracking-wide">Address</th>
                                    <th className="p-4 text-left font-semibold text-sm text-white uppercase tracking-wide">Join Date</th>
                                    <th className="p-4 text-left font-semibold text-sm text-white uppercase tracking-wide">Expiry</th>
                                    <th className="p-4 text-left font-semibold text-sm text-white uppercase tracking-wide">Status</th>
                                    <th className="p-4 text-left font-semibold text-sm text-white uppercase tracking-wide">Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {members.length === 0 ? (
                                    <tr>
                                        <td colSpan="9" className="text-center py-10">
                                            <div className="text-gray-400">
                                                <p className="text-lg mb-2.5">ðŸ‘¥ No members found</p>
                                                <p className="text-sm">Click "Add New Member" to get started!</p>
                                            </div>
                                        </td>
                                    </tr>
                                ) : (
                                    members.map((member) => (
                                        <tr key={member.Member_id} className="transition-all duration-200 hover:bg-purple-50 hover:scale-[1.01]">
                                            <td className="p-4 border-b border-gray-200 text-sm text-gray-700">
                                                <strong>#{member.Member_id}</strong>
                                            </td>
                                            <td className="p-4 border-b border-gray-200 text-sm text-gray-700">
                                                <strong>{member.Name || 'N/A'}</strong>
                                            </td>
                                            <td className="p-4 border-b border-gray-200 text-sm text-gray-700">{member.Gender || '-'}</td>
                                            <td className="p-4 border-b border-gray-200 text-sm text-gray-700">{member.Phone_Num || '-'}</td>
                                            <td className="p-4 border-b border-gray-200 text-sm text-gray-700 max-w-[200px] whitespace-nowrap overflow-hidden overflow-ellipsis">
                                                {member.Address || '-'}
                                            </td>
                                            <td className="p-4 border-b border-gray-200 text-sm text-gray-700">
                                                {member.Join_Date ? new Date(member.Join_Date).toLocaleDateString('en-US', { year: 'numeric', month: 'short', day: 'numeric' }) : 'N/A'}
                                            </td>
                                            <td className="p-4 border-b border-gray-200 text-sm text-gray-700">
                                                {member.membership_expiry ? new Date(member.membership_expiry).toLocaleDateString('en-US', { year: 'numeric', month: 'short', day: 'numeric' }) : '-'}
                                            </td>
                                            <td className="p-4 border-b border-gray-200 text-sm text-gray-700">
                                                <span className={`py-1.5 px-3.5 rounded-full text-xs font-semibold uppercase inline-block ${
                                                    member.status?.toLowerCase() === 'active' 
                                                        ? 'bg-gradient-to-r from-green-500 to-green-600 text-white' 
                                                        : 'bg-gradient-to-r from-orange-500 to-orange-600 text-white'
                                                }`}>
                                                    {member.status || 'Active'}
                                                </span>
                                            </td>
                                            <td className="p-4 border-b border-gray-200 text-sm text-gray-700">
                                                <button
                                                    onClick={() => handleEdit(member)}
                                                    title="Edit Member"
                                                    className="py-2 px-4 rounded-md font-semibold text-xs transition-all duration-200 mr-2 bg-gradient-to-r from-blue-400 to-cyan-400 text-white hover:-translate-y-0.5 hover:shadow-lg hover:shadow-blue-400/40"
                                                >
                                                    Edit
                                                </button>
                                                <button
                                                    onClick={() => handleDelete(member.Member_id)}
                                                    title="Delete Member"
                                                    className="py-2 px-4 rounded-md font-semibold text-xs transition-all duration-200 bg-gradient-to-r from-pink-400 to-red-500 text-white hover:-translate-y-0.5 hover:shadow-lg hover:shadow-red-500/40"
                                                >
                                                    Delete
                                                </button>
                                            </td>
                                        </tr>
                                    ))
                                )}
                            </tbody>
                        </table>
                    </div>
                </div>
            )}
            </div>
    );
};

export default Members;
