import React from 'react';
import { NavLink } from 'react-router-dom';

const Sidebar = () => {
    const menuItems = [
        { path: '/', icon: 'üè†', label: 'Dashboard' },
        { path: '/members', icon: 'üë•', label: 'Members' },
        { path: '/trainers', icon: 'üèãÔ∏è', label: 'Trainers' },
        { path: '/sessions', icon: 'üìÖ', label: 'Sessions' },
        { path: '/payments', icon: 'üí≥', label: 'Payments' },
        { path: '/equipment', icon: 'üèÉ', label: 'Equipment' },
        { path: '/attendance', icon: '‚úÖ', label: 'Attendance' },
        { path: '/feedback', icon: 'üí¨', label: 'Feedback' },
        { path: '/reports', icon: 'üìä', label: 'Reports' }
    ];

    return (
        <div className="w-64 h-screen fixed left-0 top-0 overflow-y-auto shadow-2xl transition-transform duration-300 z-[1000]" style={{ background: 'linear-gradient(180deg, #2c3e50 0%, #34495e 100%)' }}>
            <div className="p-6 text-center border-b-4 border-white border-opacity-10 sticky top-0 z-10" style={{ background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' }}>
                <div className="text-5xl mb-3">üí™</div>
                <h2 className="text-2xl font-bold tracking-wide text-white">Gym Manager</h2>
            </div>
            <nav className="py-5">
                <ul className="list-none p-0 m-0">
                    {menuItems.map((item) => (
                        <li key={item.path} className="mb-1 px-2.5">
                            <NavLink 
                                to={item.path} 
                                className={({ isActive }) => 
                                    `flex items-center py-3.5 px-4 text-white text-opacity-80 no-underline transition-all duration-300 text-base font-medium rounded-xl relative overflow-hidden hover:bg-white hover:bg-opacity-10 hover:text-white hover:translate-x-1 ${
                                        isActive ? 'text-white font-semibold shadow-lg' : ''
                                    }`
                                }
                                style={({ isActive }) => isActive ? { 
                                    background: 'linear-gradient(135deg, rgba(102, 126, 234, 0.3) 0%, rgba(118, 75, 162, 0.3) 100%)',
                                    boxShadow: '0 4px 15px rgba(102, 126, 234, 0.3)'
                                } : {}}
                                end={item.path === '/'}
                            >
                                <span className="text-2xl mr-4 inline-flex items-center justify-center min-w-[30px]">
                                    {item.icon}
                                </span>
                                <span className="flex-1">{item.label}</span>
                            </NavLink>
                        </li>
                    ))}
                </ul>
            </nav>
        </div>
    );
};

export default Sidebar;
